(function(){tinymce.PluginManager.requireLangPack('owlmedia');tinymce.create('tinymce.plugins.owlmediaPlugin',{init:function(ed,url){ed.addCommand('mceowlmedia',function(){ed.windowManager.open({file:url+'/dialog.php',width:500+parseInt(ed.getLang('owlmedia.delta_width',0)),height:320+parseInt(ed.getLang('owlmedia.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('owlmedia',{title:'owlmedia.desc',cmd:'mceowlmedia',image:url+'/img/example.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('owlmedia',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'owlmedia plugin',author:'OWLSite',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/owlmedia',version:"1.0"};}});tinymce.PluginManager.add('owlmedia',tinymce.plugins.owlmediaPlugin);})();